import React, {useEffect, useState} from 'react'
import './App.css'
import ArticleList from './components/ArticleList'
import Form from './components/Form'
import 'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const [articles, setArticles] = useState([])
  const [editedArticle, setEditedArticle] = useState(null)

  useEffect(()=>{
    const url = 'https://flaskreact-native-test.herokuapp.com/get'
    fetch(url, {
      method :'GET',
      headers: {
        'Content-Type': 'application/json'}
    }
  )
  .then(resp=>resp.json())
  .then(resp =>setArticles(resp))
  .catch(error => console.log(error))

  }, [])

  const editArticle = (article) =>{
    setEditedArticle(article)

  }

  const updatedData = (article) =>{
    const new_article = articles.map(my_article =>{
      if(my_article.id === article.id){
        return article
      } else {
        return my_article
      }
    })
    setArticles(new_article)
  }

  const openForm =() =>{
    setEditedArticle({title: '', body: ''})
  }

  const insertedArticle =(article) =>{
    const new_articles = [...articles, article]
    setArticles(new_articles)
  }

  const deleteArticle = (article) => {
    const new_articles = articles.filter(myarticle => {
      if(myarticle.id === article.id){
        return false;
      }
      return true
    })
    setArticles(new_articles)
  }


  return (
    <div className="App">
      <div className ="row">
        <div className="col">
        <h1>Flask and React</h1>

        </div>
        <div className="col">
        <button
        className="btn btn-success"
        onClick={openForm}
        >
          Create Article
        </button>

        </div>
      </div>

      <br/>
      <br/>
      <ArticleList articles ={articles} editArticle={editArticle} deleteArticle = {deleteArticle}/>

      {editedArticle ? <Form article = {editedArticle} updatedData={updatedData} insertedArticle = {insertedArticle}/> : null }
    </div>

  )
}

export default App
