import React, {useState} from 'react';
import Axios from 'axios';

function PostForm(props) {
    const [title, setTitle] = useState("")
    const [body, setBody] = useState("")

    const requestOption = {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        title: JSON.stringify({title:title}),
        body: JSON.stringify({body:body})
    };

    const setData = ()=>{
        fetch('https://flaskreact-n-weektwotes-rkuxmn.herokuapp.com/add', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            
        })
        .then(resp => resp.json())
        .then(data => {
            props.navigation.navigate('/')
        })
        .catch(function(error){
            console.log("error with fetch:" + error.message);
            //throws error
            throw error;
        })
    }

    return (
        <div>
            <form onSubmit={(e)=> setData(e)}>
                <input onChange={(e)=>setTitle(e)} id ='title' value={title.title} placeholder='title' type='text'></input>
                <input onChange={(e)=>setBody(e)} id ='body' value={body.body} placeholder='body' type='text'></input>
                <button>Submit</button>

            </form>
        </div>
    )
}

export default PostForm
