import React, {useState} from 'react';
import Axios from 'axios';

function PostForm() {
    const [title, setTitle] = useState("")
    const [body, setBody] = useState("")

    const setData = ()=>{
        fetch('https://flaskreact-native-test.herokuapp.com/add', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({title:title, body:body})
        })
        .then(resp => resp.json())
        // .then(data => {
        //     props.navigation.navigate('Home')
        // })
        .catch(function(error){
            console.log("error with fetch:" + error.message);
            //throws error
            throw error;
        })
    }
    function handle(e){
        const newdata ={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)

    }
    return (
        <div>
            <form onSubmit={(e)=> setData(e)}>
                <input onChange={(e)=>handle(e)} id ='title' value={title.title} placeholder='title' type='text'></input>
                <input onChange={(e)=>handle(e)} id ='body' value={data.body} placeholder='body' type='text'></input>
                <button>Submit</button>

            </form>
        </div>
    )
}

export default PostForm
