import React, {useState} from 'react';
import Axios from 'axios';

function PostForm() {
    const url = 'https://flaskreact-n-weektwotes-rkuxmn.herokuapp.com/add'
    const [data, setData] = useState({
        title: "",
        body: "",
    })

    function submit(e){
        e.preventDefault();
        Axios.post
    }
    function handle(e){
        const newdata ={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)

    }
    return (
        <div>
            <form onSubmit={(e)=> submit(e)}>
                <input onChange={(e)=>handle(e)} id ='title' value={data.title} placeholder='title' type='text'></input>
                <input onChange={(e)=>handle(e)} id ='body' value={data.body} placeholder='body' type='text'></input>
                <button>Submit</button>

            </form>
        </div>
    )
}

export default PostForm
