function PostList() {
    const [data, setData] = useState([])
    const [loading, setIsLoading] = useState(true)

    const loadData = () => {
        fetch('https://flaskreact-native-test.herokuapp.com/get', {
            method:'GET'
        })
        .then(resp => resp.json())
        .then(article =>{
            setData(article)
            setIsLoading(false)
        })
        .catch(function(error){
            console.log("error with fetch:" + error.message);
            //throws error
            throw error;
        });

    }

    useEffect(()=>{
        loadData()
    }, []);
