import React, {useState, useEffect} from 'react'

function Form(props) {

    const[title, setTitle] = useState(props.article.title)
    const[body, setBody] = useState(props.article.title)

    return (
        <div>
            {props.article ? (
                <div className = "mb-3">
                    <label htmlForm = "title" className = "form-label">
                    Title</label>
                    <input type="text" 
                    className = "form-control"
                    value = {title}
                    placeholder = "Please Enter Title"
                    onChange = {(e=> setTitle(e.target.value))}
                    />
                    <label htmlForm = "body" className = "form-label">
                    Article</label>
                    <textarea
                    row = "5"
                    className = "form-control"
                    value = {body}
                    placeholder ="Please provide a description"
                    onChange = {(e=> setBody(e.target.value))}/>
                    <button>
                        Submit Update
                    </button>

                </div>
            ):null};
        </div>
    )
}

export default Form
